import React, { useState } from 'react';
import { useLocation, Link } from "react-router-dom";
import CustomButton from '../../UI/CustomButton/CustomButton';
import "./SingleIdea.css"

import { connect, useSelector, useDispatch } from 'react-redux';

export class SingleIdea extends React.Component  {
    constructor(props) {
        super(props);
      }
      render(){
    const id = window.location.pathname.split('/').slice(-1)[0];
    const idea = this.props.ideas[id-1];
    console.log(idea)
    console.log(id);
    return(
        <div>
            <div className="container">
                <div className="row">

                <div className="col-6">

                <div className="row">
                    <img className="founder-img" src={require('../../UI/ItemSummary/' + idea.imgSource + '.jpg')}/>
                </div>
                <div className="row">
                    <h1>{idea.title}</h1>
                </div>
                <div className="row">
                    {idea.founderInfo}
                </div></div>
                <div className="col-6">
                    <div className="row">
                        <h4>Idea Description</h4>
                        <p>{idea.long}</p>
                    </div>
                    <div className="row">
                        <h4>What we need</h4>
                        <p>{idea.need}</p>
                    </div>
                </div>
            </div>
            <div className="row">
                <h3>Stage</h3>
                <h4>{idea.phase}</h4>
            </div>
            </div>
        </div>
    )
}
}
const  mapStateToProps = (state) => {
    return {
        ideas: state.ideas
    };
}

export default connect(mapStateToProps)(SingleIdea);
