import React from 'react';
import './CustomButton.css';
import classnames from 'classnames';
const CustomButton = ({
    title,
    onPress,
    buttonColor,
    titleColor,
    buttonStyle,
    textStyle,
    buttonAlternate,
    customClassName
}) => {
    return (
        <div
            className={buttonAlternate ? 'ButtonClass2' : 'ButtonClass'}
            style={{
                backgroundColor: buttonColor,
                border: buttonAlternate ? '' : '5px solid ' + titleColor,
                ...buttonStyle
            }}
            onPress={onPress}>
            <p className={classnames(buttonAlternate ? 'pClass2' + customClassName: 'pClass', customClassName)} style={{
                'color': buttonAlternate ? '' :  titleColor,
                fontFamily: 'Spinnaker',
                fontSize: '80%', ...textStyle
            }}>
                <strong>{title}</strong>
            </p>
        </div>
    );
};

export default CustomButton;

