import React from 'react';
import './Card.css';

const Card = ({
    title,
    text,
    onPress,
    buttonColor,
    titleColor,
    buttonStyle,
    textStyle,
}) => {
    return (
        <div
            className='CardClass'
            style={{
                backgroundColor: buttonColor,
                border: '5px solid ' + titleColor,
                ...buttonStyle
            }}
            onPress={onPress}>
            <h2 className='chClass' style={{
                'color': titleColor,
                fontFamily: 'Spinnaker',
                fontSize: '80%', ...textStyle
            }}>
                {title}
            </h2>
            <p className='cpClass' style={{
                'color': titleColor,
                fontFamily: 'Spinnaker',
                fontSize: '80%', ...textStyle
            }}>
                {text}
            </p>

        </div>
    );
};

export default Card;
