import React from 'react';
import './ItemSummary.css';
import classnames from 'classnames';
import CustomButton from '../CustomButton/CustomButton';
import logo from './A.jpg';

const ItemSummary = ({
    title,
    onPress,
    ItemSummaryColor,
    titleColor,
    ItemSummaryStyle,
    textStyle,
    ItemSummaryAlternate,
    customClassName
}) => {
    const bitLower = {position:'relative', top: '20px'}
    return (
        <div
            className={classnames(ItemSummaryAlternate ? 'ItemSummaryClass2' : 'ItemSummaryClass', customClassName)}
            style={{
                backgroundColor: ItemSummaryColor,
                border: ItemSummaryAlternate ? '' : '5px solid ' + titleColor,
                ...ItemSummaryStyle
            }}
            onClick={onPress}
        // onPress={onPress}
        >
            <div className="container">
                <div className="row">
                    <h3>Idea Description</h3>
                </div>
                <div className="row">
                    <div className="col-2">
                        <CustomButton textStyle={{ position: 'relative', top: "0.5vh"}} buttonAlternate={true} title="+ Read More" buttonStyle={{marginLeft: '-15px', height:'6vh', width:'20vh', position: 'relative', bottom: "1vh"}}></CustomButton>
                    </div>
                    <div className="col-2">
                        <img src={logo} className="founder-logo" alt="logo" />  
                    </div>
                    <div className="col-2" style={bitLower}>
                    <div className="row">
                        <h4 style={{color:'#75AF33'}}>Founder's Info</h4>
                    </div>
                    <div className="row">
                        <p className='ipClass'>Jannik Moensch</p>
                    </div>
                    </div>
                    <div className="col-2" style={bitLower}>
                    <div className="row">
                        <h4 style={{color:'#75AF33'}}>Campus</h4>
                    </div>
                    <div className="row">
                        <p className='ipClass'>SMU</p>
                    </div>
                    </div>
                    <div className="col-2" style={bitLower}>
                    <div className="row">
                        <h4 style={{color:'#75AF33'}}>Stage</h4>
                    </div>
                    <div className="row">
                        <p className='ipClass'>Idea (Phase 1)</p>
                    </div>
                    </div>
                    <div className="col-2" style={bitLower}>
                    <div className="row">
                        <h4 style={{color:'#75AF33'}}>Likes</h4>
                    </div>
                    <div className="row">
                        <p className='ipClass'>350</p>
                    </div>
                    </div>

                    <p className={ItemSummaryAlternate ? 'pClass2' : 'pClass'} style={{
                        'color': ItemSummaryAlternate ? '' : titleColor,
                        fontFamily: 'Spinnaker',
                        fontSize: '80%', ...textStyle
                    }}>
                        <strong>{title}</strong>
                    </p>

                </div>
            </div>

        </div>
    );
};

export default ItemSummary;

