import React from 'react';
import './Navlink.css';
import { useSelector, useDispatch } from 'react-redux';


const Navlink = ({
    text,
    tag,
    onPress,
    titleColor,
    buttonStyle,
    textStyle,
}) => {
    const page = useSelector(state => state.page);
    const dispatch = useDispatch();
    const changePage = (value) => {
        dispatch({ type: 'pageChange', value})
    }
    return (
        <div className='Navlink' >
            <p onClick={() => changePage(tag)} className="navP" style={page == tag ? {color:'#75AF33', fontFamily: 'Spinnaker', fontSize: 'calc(2vmin - 2px)' } : { fontFamily: 'Spinnaker', fontSize: 'calc(2vmin - 2px)' }}>{text}</p>
        </div>

    );
};

export default Navlink;
