import React, { useState } from 'react';
import { useLocation, Link } from "react-router-dom";
import CustomButton from '../../UI/CustomButton/CustomButton';
import "./SingleIdea.css"

import { connect, useSelector, useDispatch } from 'react-redux';

export class SingleIdea extends React.Component  {
    constructor(props) {
        super(props);
      }
      render(){
    const id = window.location.pathname.split('/').slice(-1)[0];
    const idea = this.props.ideas[id-1];
    console.log(idea)
    console.log(id);
    return(
        <div>
            <div className="container">
                <div className="row">
                    <img src={require('../../UI/ItemSummary' + idea.imgSource + '.jpg')}/>
                </div>
                <div className="row">
                    <h1>{idea.title}</h1>
                </div>
                <div className="row">
                    {idea.founderInfo}
                </div>
            </div>
            this is the idea with the id:
            {id}
        </div>
    )
}
}
const  mapStateToProps = (state) => {
    return {
        ideas: state.ideas
    };
}

export default connect(mapStateToProps)(SingleIdea);
