import React from 'react';
import CustomButton from '../CustomButton/CustomButton';
import './Card.css';

const Card = ({
    title,
    text,
    onPress,
    cardColor,
    titleColor,
    buttonStyle,
    textStyle,
    ButtonText
}) => {
    return (
        <div
            className='CardClass'
            style={{
                backgroundColor: cardColor,
                border: '5px solid ' + titleColor,
                ...buttonStyle
            }}
            onPress={onPress}>
            <h2 className='chClass' style={{
                'color': titleColor,
                fontFamily: 'Spinnaker',
                ...textStyle
            }}>
                {title}
            </h2>
            <p className='cpClass' style={{
                'color': titleColor,
                fontFamily: 'Spinnaker',
                ...textStyle
            }}>
                {text}
            </p>
            <CustomButton title={ButtonText} titleColor="black" textStyle={{fontSize: '2vh'}} buttonStyle={{ minHeight: '3vh', minWidth: "12vh", border: '2px solid black', backgroundColor: 'white', position : 'absolute', bottom:'3vh'}}>

            </CustomButton>

        </div>
    );
};

export default Card;
