import React from 'react';
import './CustomButton.css';

const CustomButton = ({
    title,
    onPress,
    buttonColor,
    titleColor,
    buttonStyle,
    textStyle,
  }) => {
    return (
      <div
        className='ButtonClass'
        style={{
            border : ['10 px solid', ' ', titleColor].join(), 
            backgroundColor : buttonColor,
    }}
        onPress={onPress}>
        <p className='pClass' style={{'color': titleColor}}>
          <strong>{title}</strong>
        </p>
      </div>
    );
  };

  export default CustomButton;

