import React from 'react';
import './Card.css';

const Card = ({
    title,
    onPress,
    buttonColor,
    titleColor,
    buttonStyle,
    textStyle,
}) => {
    return (
        <div
            className='CardClass'
            style={{
                backgroundColor: buttonColor,
                border: '5px solid ' + titleColor,
                ...buttonStyle
            }}
            onPress={onPress}>
            <h2 className='cpClass' style={{
                'color': titleColor,
                fontFamily: 'Spinnaker', ...textStyle
            }}>
                <strong>{title}</strong>
            </h2>
        </div>
    );
};

export default Card;
